import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useNavigate } from "react-router-dom";


const CropPrediction = () => {
    const [formData, setFormData] = useState({
        N: "",
        P: "",
        K: "",
        temperature: "",
        humidity: "",
        ph: "",
        rainfall: "",
    });
    const navigate = useNavigate();

    const [cropDetails, setCropDetails] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [showResults, setShowResults] = useState(false);
    const [visiblePoints, setVisiblePoints] = useState([]);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setCropDetails(null);
        setError(null);
        setLoading(true);
        setShowResults(false);
        setVisiblePoints([]);

        try {
            // First, get the prediction from the predict endpoint
            const predictResponse = await fetch("http://localhost:5000/api/auth/predict", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    N: parseFloat(formData.N),
                    P: parseFloat(formData.P),
                    K: parseFloat(formData.K),
                    temperature: parseFloat(formData.temperature),
                    humidity: parseFloat(formData.humidity),
                    ph: parseFloat(formData.ph),
                    rainfall: parseFloat(formData.rainfall),
                }),
            });

            const predictData = await predictResponse.json();
            
            if (!predictResponse.ok) {
                setLoading(false);
                setError(predictData.message || "Error predicting crop");
                return;
            }
            
            const cropName = predictData.recommended_crop;
            
            // Now fetch the crop details using the cropName
            const detailsResponse = await fetch(`http://localhost:5000/api/auth/get_crop/${cropName}`);
            const detailsData = await detailsResponse.json();
            
            setLoading(false);
            
            if (detailsResponse.ok) {
                setCropDetails({
                    name: cropName,
                    details: detailsData,
                    // Using a placeholder image to ensure it loads
                    imagePath: `/api/placeholder/250/250`
                });
                // Trigger the animation after successful prediction with a longer delay
                setTimeout(() => setShowResults(true), 300);
            } else {
                setError("Error fetching crop details");
            }
        } catch (err) {
            setLoading(false);
            setError("Failed to connect to the backend");
        }
    };

    // Format the crop details as points
    const formatDetailsAsPoints = (details) => {
        if (!details || !details.description) return [];
        
        try {
            const parsedData = JSON.parse(details.description);
            let points = [];
            
            // Extract all points from each category
            Object.keys(parsedData).forEach(category => {
                const categoryData = parsedData[category];
                
                Object.values(categoryData).forEach(point => {
                    points.push(point);
                });
            });
            
            return points;
        } catch (err) {
            console.error("Error parsing crop details:", err);
            return ["Unable to parse crop details"];
        }
    };

    // Effect to gradually reveal the crop information points
    useEffect(() => {
        if (cropDetails && showResults) {
            const points = formatDetailsAsPoints(cropDetails.details);
            const totalPoints = points.length;
            
            if (totalPoints > 0) {
                const intervalTime = 500; // Time between each point appearing
                
                const interval = setInterval(() => {
                    setVisiblePoints(prev => {
                        const newCount = prev.length + 1;
                        if (newCount >= totalPoints) {
                            clearInterval(interval);
                            return points;
                        }
                        return points.slice(0, newCount);
                    });
                }, intervalTime);
                
                return () => clearInterval(interval);
            }
        }
    }, [cropDetails, showResults]);

    return (
        <div style={styles.pageContainer}>
            {/* Home Button */}
            <motion.button 
                onClick={() => navigate("/home")} 
                style={styles.homeButton}
                whileHover={{ scale: 1.05, backgroundColor: "rgba(255, 123, 66, 0.2)" }}
                whileTap={{ scale: 0.95 }}
            >
                ‚¨Ö Home
            </motion.button>

            <div style={styles.contentContainer}>
                {/* Form Component */}
                <motion.div
                    style={styles.formContainer}
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ 
                        opacity: 1, 
                        y: 0,
                        x: showResults ? "-100%" : "0%",
                    }}
                    transition={{ 
                        duration: 0.8, 
                        ease: "easeInOut",
                        x: { duration: 1.2, type: "spring", stiffness: 100 } 
                    }}
                >
                    <motion.h1 
                        style={styles.header}
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        transition={{ delay: 0.3 }}
                    >
                        üå± Crop Prediction
                    </motion.h1>
                    
                    <form style={styles.form} onSubmit={handleSubmit}>
                        <div style={styles.row}>
                            {["N", "P", "K"].map((key) => (
                                <div key={key} style={styles.inputGroup}>
                                    <label style={styles.label} htmlFor={key}>
                                        {key}:
                                    </label>
                                    <motion.input
                                        type="number"
                                        id={key}
                                        name={key}
                                        value={formData[key]}
                                        onChange={handleChange}
                                        style={styles.input}
                                        required
                                        whileFocus={{ scale: 1.03, borderColor: "#ffaa77" }}
                                    />
                                </div>
                            ))}
                        </div>

                        {["temperature", "humidity", "ph", "rainfall"].map((key, index) => (
                            <div key={key} style={styles.inputGroup}>
                                <label style={styles.label} htmlFor={key}>
                                    {key.charAt(0).toUpperCase() + key.slice(1)}:
                                </label>
                                <motion.input
                                    type="number"
                                    id={key}
                                    name={key}
                                    value={formData[key]}
                                    onChange={handleChange}
                                    style={styles.input}
                                    required
                                    initial={{ opacity: 0, y: 20 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    transition={{ delay: 0.1 + index * 0.1 }}
                                    whileFocus={{ scale: 1.03, borderColor: "#ffaa77" }}
                                />
                            </div>
                        ))}

                        <motion.button
                            type="submit"
                            style={styles.submitButton}
                            disabled={loading}
                            whileHover={{ scale: 1.05, backgroundColor: "#ff9a70" }}
                            whileTap={{ scale: 0.95 }}
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            transition={{ delay: 0.5 }}
                        >
                            {loading ? (
                                <motion.div
                                    style={styles.loadingContainer}
                                    initial={{ width: 0 }}
                                    animate={{ width: "100%" }}
                                    transition={{ duration: 2, repeat: Infinity }}
                                >
                                    <span style={styles.loadingText}>Processing...</span>
                                </motion.div>
                            ) : (
                                "Predict Crop"
                            )}
                        </motion.button>
                    </form>

                    <AnimatePresence>
                        {error && (
                            <motion.div
                                style={styles.errorContainer}
                                initial={{ scale: 0.8, opacity: 0 }}
                                animate={{ scale: 1, opacity: 1 }}
                                exit={{ scale: 0.8, opacity: 0 }}
                                transition={{ duration: 0.3 }}
                            >
                                <h2 style={styles.error}>‚ö†Ô∏è {error}</h2>
                            </motion.div>
                        )}
                    </AnimatePresence>
                </motion.div>

                {/* Results Component */}
                <AnimatePresence>
                    {cropDetails && (
                        <motion.div
                            style={styles.resultContainer}
                            initial={{ opacity: 0, x: "100%" }}
                            animate={{ 
                                opacity: showResults ? 1 : 0, 
                                x: showResults ? "0%" : "100%",
                            }}
                            exit={{ opacity: 0, x: "100%" }}
                            transition={{ 
                                duration: 1, 
                                ease: "easeInOut",
                                delay: 0.5,
                                type: "spring", 
                                stiffness: 80 
                            }}
                        >
                            <motion.h2 
                                style={styles.result}
                                initial={{ opacity: 0, y: -20 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ delay: 1.2 }}
                            >
                                üåæ Recommended Crop
                            </motion.h2>
                            
                            <motion.p 
                                style={styles.cropName}
                                initial={{ opacity: 0, scale: 0.8 }}
                                animate={{ opacity: 1, scale: 1 }}
                                transition={{ delay: 1.5, duration: 0.5 }}
                            >
                                {cropDetails.name}
                            </motion.p>
                            
                            <motion.div 
                                style={styles.detailsContainer}
                                initial={{ opacity: 0, y: 30 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ delay: 1.8, duration: 0.5 }}
                            >
                                <motion.div 
                                    style={styles.imageContainer}
                                    initial={{ opacity: 0, scale: 0.8 }}
                                    animate={{ opacity: 1, scale: 1 }}
                                    transition={{ delay: 2.0, duration: 0.5 }}
                                >
                                    <img 
                                        src={cropDetails.imagePath} 
                                        alt={cropDetails.name}
                                        style={styles.cropImage}
                                    />
                                </motion.div>
                                
                                <div style={styles.cropInfo}>
                                    {visiblePoints.map((point, index) => (
                                        <motion.div 
                                            key={index} 
                                            style={styles.pointItem}
                                            initial={{ opacity: 0, x: 20 }}
                                            animate={{ opacity: 1, x: 0 }}
                                            transition={{ duration: 0.5 }}
                                        >
                                            <motion.span 
                                                style={styles.bulletPoint}
                                                initial={{ scale: 0 }}
                                                animate={{ scale: 1 }}
                                                transition={{ duration: 0.3 }}
                                            >
                                                ‚Ä¢
                                            </motion.span>
                                            <span style={styles.pointText}>{point}</span>
                                        </motion.div>
                                    ))}
                                    
                                    {/* Display a typing indicator if there are more points to show */}
                                    {cropDetails && showResults && visiblePoints.length < formatDetailsAsPoints(cropDetails.details).length && (
                                        <motion.div 
                                            style={styles.typingIndicator}
                                            animate={{ opacity: [0.3, 1, 0.3] }}
                                            transition={{ duration: 1.2, repeat: Infinity }}
                                        >
                                            <span>‚Ä¢</span>
                                            <span>‚Ä¢</span>
                                            <span>‚Ä¢</span>
                                        </motion.div>
                                    )}
                                </div>
                            </motion.div>
                        </motion.div>
                    )}
                </AnimatePresence>
            </div>
        </div>
    );
};

