import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useNavigate } from "react-router-dom";
import riceImage from '../Images/rice.png';

const CropPrediction = () => {
    const [formData, setFormData] = useState({
        N: "",
        P: "",
        K: "",
        temperature: "",
        humidity: "",
        ph: "",
        rainfall: "",
    });
    const navigate = useNavigate();

    const [cropDetails, setCropDetails] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [showResults, setShowResults] = useState(false);
    const [visiblePoints, setVisiblePoints] = useState([]);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setCropDetails(null);
        setError(null);
        setLoading(true);
        setShowResults(false);
        setVisiblePoints([]);

        try {
            // First, get the prediction from the predict endpoint
            const predictResponse = await fetch("http://localhost:5000/api/auth/predict", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    N: parseFloat(formData.N),
                    P: parseFloat(formData.P),
                    K: parseFloat(formData.K),
                    temperature: parseFloat(formData.temperature),
                    humidity: parseFloat(formData.humidity),
                    ph: parseFloat(formData.ph),
                    rainfall: parseFloat(formData.rainfall),
                }),
            });

            const predictData = await predictResponse.json();
            
            if (!predictResponse.ok) {
                setLoading(false);
                setError(predictData.message || "Error predicting crop");
                return;
            }
            
            const cropName = predictData.recommended_crop;
            
            // Now fetch the crop details using the cropName
            const detailsResponse = await fetch(`http://localhost:5000/api/auth/get_crop/${cropName}`);
            const detailsData = await detailsResponse.json();
            
            setLoading(false);
            
            if (detailsResponse.ok) {
                setCropDetails({
                    name: cropName,
                    details: detailsData,
                    imagePath: cropName.toLowerCase() === "rice" 
                        ? riceImage 
                        : `/api/placeholder/250/250`
                });
                // Trigger the animation after successful prediction with a longer delay
                setTimeout(() => setShowResults(true), 300);
            } else {
                setError("Error fetching crop details");
            }
        } catch (err) {
            setLoading(false);
            setError("Failed to connect to the backend");
        }
    };

    // Format the crop details as points
    const formatDetailsAsPoints = (details) => {
        if (!details || !details.description) return [];
        
        try {
            const parsedData = JSON.parse(details.description);
            let points = [];
            
            // Extract all points from each category
            Object.keys(parsedData).forEach(category => {
                const categoryData = parsedData[category];
                
                Object.values(categoryData).forEach(point => {
                    points.push(point);
                });
            });
            
            return points;
        } catch (err) {
            console.error("Error parsing crop details:", err);
            return ["Unable to parse crop details"];
        }
    };

    // Effect to gradually reveal the crop information points
    useEffect(() => {
        if (cropDetails && showResults) {
            const points = formatDetailsAsPoints(cropDetails.details);
            const totalPoints = points.length;
            
            if (totalPoints > 0) {
                const intervalTime = 500; // Time between each point appearing
                
                const interval = setInterval(() => {
                    setVisiblePoints(prev => {
                        const newCount = prev.length + 1;
                        if (newCount >= totalPoints) {
                            clearInterval(interval);
                            return points;
                        }
                        return points.slice(0, newCount);
                    });
                }, intervalTime);
                
                return () => clearInterval(interval);
            }
        }
    }, [cropDetails, showResults]);

    return (
        <div style={styles.pageContainer}>
            {/* Farm Field Background Overlay */}
            <div style={styles.backgroundOverlay}></div>
            
            {/* Animated plants */}
            <motion.div 
                style={{...styles.plantDecoration, left: '5%', bottom: '10%'}}
                animate={{ y: [0, -5, 0] }}
                transition={{ duration: 3, repeat: Infinity, ease: "easeInOut" }}
            >
                🌿
            </motion.div>
            <motion.div 
                style={{...styles.plantDecoration, left: '15%', bottom: '5%'}}
                animate={{ y: [0, -5, 0] }}
                transition={{ duration: 2.5, repeat: Infinity, ease: "easeInOut", delay: 0.5 }}
            >
                🌱
            </motion.div>
            <motion.div 
                style={{...styles.plantDecoration, right: '10%', bottom: '12%'}}
                animate={{ y: [0, -5, 0] }}
                transition={{ duration: 3.2, repeat: Infinity, ease: "easeInOut", delay: 0.8 }}
            >
                🌾
            </motion.div>
            <motion.div 
                style={{...styles.plantDecoration, right: '20%', bottom: '7%'}}
                animate={{ y: [0, -5, 0] }}
                transition={{ duration: 2.8, repeat: Infinity, ease: "easeInOut", delay: 1.2 }}
            >
                🍃
            </motion.div>

            {/* Home Button */}
            <motion.button 
                onClick={() => navigate("/home")} 
                style={styles.homeButton}
                whileHover={{ scale: 1.05, backgroundColor: "rgba(59, 122, 87, 0.2)" }}
                whileTap={{ scale: 0.95 }}
            >
                <span style={{marginRight: "8px"}}>🏡</span> Back to Farm
            </motion.button>

            <div style={styles.contentContainer}>
                {/* FarmOS Branding - Shows initially and fades out when results appear */}
                <AnimatePresence>
                    {!showResults && (
                        <motion.div 
                            style={styles.brandingContainer}
                            initial={{ opacity: 1, scale: 1 }}
                            animate={{ opacity: showResults ? 0 : 1, scale: showResults ? 0.9 : 1 }}
                            exit={{ opacity: 0, scale: 0.9 }}
                            transition={{ duration: 0.8 }}
                        >
                            <motion.h1 
                                style={styles.brandingTitle}
                                initial={{ y: -20, opacity: 0 }}
                                animate={{ y: 0, opacity: 1 }}
                                transition={{ delay: 0.3, duration: 0.8 }}
                            >
                                <span style={styles.brandHighlight}>Farm</span>OS
                            </motion.h1>
                            <motion.div 
                                style={styles.brandingTitleUnderline}
                                initial={{ width: 0 }}
                                animate={{ width: "90%" }}
                                transition={{ delay: 0.6, duration: 1.2 }}
                            />
                            <motion.h2 
                                style={styles.brandingSubtitle}
                                initial={{ y: 20, opacity: 0 }}
                                animate={{ y: 0, opacity: 1 }}
                                transition={{ delay: 0.8, duration: 0.8 }}
                            >
                                Your Perfect Farming Advisor
                            </motion.h2>
                            <motion.p 
                                style={styles.brandingDescription}
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                transition={{ delay: 1.2, duration: 0.8 }}
                            >
                                Enter your soil and climate data to discover the ideal crop for your farm. 
                                Our advanced algorithm analyzes your conditions to provide personalized recommendations.
                            </motion.p>
                            <motion.div 
                                style={styles.brandingIcons}
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                transition={{ delay: 1.6, duration: 0.8 }}
                            >
                                <span style={styles.iconLarge}>🌾</span>
                                <span style={styles.iconLarge}>🌱</span>
                                <span style={styles.iconLarge}>🌿</span>
                            </motion.div>
                        </motion.div>
                    )}
                </AnimatePresence>

                {/* Form Component */}
                <motion.div
                    style={styles.formContainer}
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ 
                        opacity: 1, 
                        y: 0,
                        x: showResults ? "-100%" : "0%",
                    }}
                    transition={{ 
                        duration: 0.8, 
                        ease: "easeInOut",
                        x: { duration: 1.2, type: "spring", stiffness: 100 } 
                    }}
                >
                    <motion.h1 
                        style={styles.header}
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        transition={{ delay: 0.3 }}
                    >
                        🌱 Crop Advisor
                    </motion.h1>
                    
                    <p style={styles.subHeader}>Enter your soil and weather conditions to find the perfect crop</p>
                    
                    <form style={styles.form} onSubmit={handleSubmit}>
                        <div style={styles.formSection}>
                            <h3 style={styles.sectionTitle}>🧪 Soil Nutrients (mg/kg)</h3>
                            <div style={styles.row}>
                                {[
                                    { key: "N", name: "Nitrogen" },
                                    { key: "P", name: "Phosphorus" },
                                    { key: "K", name: "Potassium" }
                                ].map((nutrient) => (
                                    <div key={nutrient.key} style={styles.inputGroup}>
                                        <label style={styles.label} htmlFor={nutrient.key}>
                                            {nutrient.name}:
                                        </label>
                                        <motion.input
                                            type="number"
                                            id={nutrient.key}
                                            name={nutrient.key}
                                            value={formData[nutrient.key]}
                                            onChange={handleChange}
                                            style={styles.input}
                                            required
                                            whileFocus={{ scale: 1.03, borderColor: "#55c57a" }}
                                        />
                                    </div>
                                ))}
                            </div>
                        </div>

                        <div style={styles.formSection}>
                            <h3 style={styles.sectionTitle}>☁️ Environmental Factors</h3>
                            {[
                                { key: "temperature", name: "Temperature (°C)", icon: "🌡️" },
                                { key: "humidity", name: "Humidity (%)", icon: "💧" },
                                { key: "ph", name: "Soil pH", icon: "⚗️" },
                                { key: "rainfall", name: "Annual Rainfall (mm)", icon: "🌧️" }
                            ].map((factor, index) => (
                                <div key={factor.key} style={styles.inputGroup}>
                                    <label style={styles.label} htmlFor={factor.key}>
                                        {factor.icon} {factor.name}:
                                    </label>
                                    <motion.input
                                        type="number"
                                        id={factor.key}
                                        name={factor.key}
                                        value={formData[factor.key]}
                                        onChange={handleChange}
                                        style={styles.input}
                                        required
                                        initial={{ opacity: 0, y: 20 }}
                                        animate={{ opacity: 1, y: 0 }}
                                        transition={{ delay: 0.1 + index * 0.1 }}
                                        whileFocus={{ scale: 1.03, borderColor: "#55c57a" }}
                                    />
                                </div>
                            ))}
                        </div>

                        <motion.button
                            type="submit"
                            style={styles.submitButton}
                            disabled={loading}
                            whileHover={{ scale: 1.05, backgroundColor: "#46a55e" }}
                            whileTap={{ scale: 0.95 }}
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            transition={{ delay: 0.5 }}
                        >
                            {loading ? (
                                <motion.div
                                    style={styles.loadingContainer}
                                    initial={{ width: 0 }}
                                    animate={{ width: "100%" }}
                                    transition={{ duration: 2, repeat: Infinity }}
                                >
                                    <span style={styles.loadingText}>Analyzing soil and climate data...</span>
                                </motion.div>
                            ) : (
                                <>🔍 Find Ideal Crop</>
                            )}
                        </motion.button>
                    </form>

                    <AnimatePresence>
                        {error && (
                            <motion.div
                                style={styles.errorContainer}
                                initial={{ scale: 0.8, opacity: 0 }}
                                animate={{ scale: 1, opacity: 1 }}
                                exit={{ scale: 0.8, opacity: 0 }}
                                transition={{ duration: 0.3 }}
                            >
                                <h2 style={styles.error}>⚠️ {error}</h2>
                            </motion.div>
                        )}
                    </AnimatePresence>
                </motion.div>

                {/* Results Component */}
                <AnimatePresence>
                    {cropDetails && (
                        <motion.div
                            style={styles.resultContainer}
                            initial={{ opacity: 0, x: "100%" }}
                            animate={{ 
                                opacity: showResults ? 1 : 0, 
                                x: showResults ? "0%" : "100%",
                            }}
                            exit={{ opacity: 0, x: "100%" }}
                            transition={{ 
                                duration: 1, 
                                ease: "easeInOut",
                                delay: 0.5,
                                type: "spring", 
                                stiffness: 80 
                            }}
                        >
                            <motion.div
                                style={styles.resultHeader}
                                initial={{ opacity: 0, y: -20 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ delay: 1.2 }}
                            >
                                <motion.div 
                                    style={styles.resultIcon}
                                    animate={{ rotate: [0, 10, -10, 0] }}
                                    transition={{ duration: 1.5, delay: 1.5 }}
                                >
                                    🌾
                                </motion.div>
                                <h2 style={styles.resultTitle}>
                                    Perfect Crop Match
                                </h2>
                            </motion.div>
                            
                            <motion.div 
                                style={styles.cropNameWrapper}
                                initial={{ opacity: 0, scale: 0.8 }}
                                animate={{ opacity: 1, scale: 1 }}
                                transition={{ delay: 1.5, duration: 0.5 }}
                            >
                                <p style={styles.cropName}>{cropDetails.name}</p>
                                <motion.div 
                                    style={styles.cropNameUnderline}
                                    initial={{ width: 0 }}
                                    animate={{ width: "80%" }}
                                    transition={{ delay: 1.7, duration: 0.8 }}
                                />
                            </motion.div>
                            
                            <motion.div 
                                style={styles.detailsContainer}
                                initial={{ opacity: 0, y: 30 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ delay: 1.8, duration: 0.5 }}
                            >
                                <motion.div 
                                    style={styles.imageContainer}
                                    initial={{ opacity: 0, scale: 0.8 }}
                                    animate={{ opacity: 1, scale: 1 }}
                                    transition={{ delay: 2.0, duration: 0.5 }}
                                >
                                    <img 
                                        src={cropDetails.imagePath} 
                                        alt={cropDetails.name}
                                        style={styles.cropImage}
                                    />
                                    <div style={styles.imageBorder} />
                                </motion.div>
                                
                                <div style={styles.cropInfo}>
                                    <h3 style={styles.cropInfoTitle}>Growing Information:</h3>
                                    {visiblePoints.map((point, index) => (
                                        <motion.div 
                                            key={index} 
                                            style={styles.pointItem}
                                            initial={{ opacity: 0, x: 20 }}
                                            animate={{ opacity: 1, x: 0 }}
                                            transition={{ duration: 0.5 }}
                                        >
                                            <motion.span 
                                                style={styles.bulletPoint}
                                                initial={{ scale: 0 }}
                                                animate={{ scale: 1 }}
                                                transition={{ duration: 0.3 }}
                                            >
                                                🌱
                                            </motion.span>
                                            <span style={styles.pointText}>{point}</span>
                                        </motion.div>
                                    ))}
                                    
                                    {/* Display a typing indicator if there are more points to show */}
                                    {cropDetails && showResults && visiblePoints.length < formatDetailsAsPoints(cropDetails.details).length && (
                                        <motion.div 
                                            style={styles.typingIndicator}
                                            animate={{ opacity: [0.3, 1, 0.3] }}
                                            transition={{ duration: 1.2, repeat: Infinity }}
                                        >
                                            <span>•</span>
                                            <span>•</span>
                                            <span>•</span>
                                        </motion.div>
                                    )}
                                </div>
                            </motion.div>

                            <motion.button
                                style={styles.newSearchButton}
                                onClick={() => {
                                    setShowResults(false);
                                    setTimeout(() => {
                                        setCropDetails(null);
                                        setFormData({
                                            N: "",
                                            P: "",
                                            K: "",
                                            temperature: "",
                                            humidity: "",
                                            ph: "",
                                            rainfall: "",
                                        });
                                    }, 800);
                                }}
                                whileHover={{ scale: 1.05, backgroundColor: "rgba(59, 122, 87, 0.2)" }}
                                whileTap={{ scale: 0.95 }}
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                transition={{ delay: 2.5 }}
                            >
                                🔄 Try Another Analysis
                            </motion.button>
                        </motion.div>
                    )}
                </AnimatePresence>
            </div>
        </div>
    );
};

