import React, { useState } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import "./CropPrediction.css";

const CropPrediction = () => {
    const [formData, setFormData] = useState({
        N: "",
        P: "",
        K: "",
        temperature: "",
        humidity: "",
        ph: "",
        rainfall: "",
    });

    const navigate = useNavigate();

    const [recommendedCrop, setRecommendedCrop] = useState(null);
    const [cropImage, setCropImage] = useState(null);
    const [cropDetails, setCropDescription] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [formShifted, setFormShifted] = useState(false);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setRecommendedCrop(null);
        setError(null);
        setLoading(true);

        try {
            // Step 1: Predict Crop
            const predictResponse = await fetch("http://localhost:5000/api/auth/predict", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    N: parseFloat(formData.N),
                    P: parseFloat(formData.P),
                    K: parseFloat(formData.K),
                    temperature: parseFloat(formData.temperature),
                    humidity: parseFloat(formData.humidity),
                    ph: parseFloat(formData.ph),
                    rainfall: parseFloat(formData.rainfall),
                }),
            });

            const predictData = await predictResponse.json();

            if (!predictResponse.ok) {
                setLoading(false);
                setError(predictData.message || "Error predicting crop");
                return;
            }

            const cropName = predictData.recommended_crop;
            setRecommendedCrop(cropName);

            // Step 2: Fetch Crop Details
            const detailsResponse = await fetch(`http://localhost:5001/api/auth/get_crop/${cropName}`);
            const detailsData = await detailsResponse.json();

            if (!detailsResponse.ok) {
                setLoading(false);
                setError(detailsData.message || "Error fetching crop details");
                return;
            }

            setCropDescription(JSON.parse(detailsData.description));
            setFormShifted(true);
            setLoading(false);
        } catch (err) {
            setLoading(false);
            setError("Failed to connect to the backend");
        }
    };

    return (
        <div className="page-container">
            {/* Home Button */}
            <button onClick={() => navigate("/home")} className="home-button">
                ⬅ Home
            </button>

            {/* Form Animation */}
            <motion.div
                className={`form-container ${formShifted ? "shifted" : ""}`}
                initial={{ x: 0, opacity: 1 }}
                animate={{
                    x: formShifted ? "-50%" : 0,
                    opacity: 1,
                }}
                transition={{ duration: 0.5 }}
            >
                <h1 className="header">🌱 Crop Prediction</h1>

                <form className="form" onSubmit={handleSubmit}>
                    <div className="row">
                        {["N", "P", "K"].map((key) => (
                            <div key={key} className="input-group">
                                <label htmlFor={key}>{key}:</label>
                                <input
                                    type="number"
                                    id={key}
                                    name={key}
                                    value={formData[key]}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                        ))}
                    </div>

                    {["temperature", "humidity", "ph", "rainfall"].map((key) => (
                        <div key={key} className="input-group">
                            <label htmlFor={key}>
                                {key.charAt(0).toUpperCase() + key.slice(1)}:
                            </label>
                            <input
                                type="number"
                                id={key}
                                name={key}
                                value={formData[key]}
                                onChange={handleChange}
                                required
                            />
                        </div>
                    ))}

                    <motion.button
                        type="submit"
                        className="submit-button"
                        disabled={loading}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                    >
                        {loading ? "Predicting..." : "Predict Crop"}
                    </motion.button>
                </form>
            </motion.div>

            {/* Prediction Result */}
            {recommendedCrop && (
                <motion.div
                    className="result-container"
                    initial={{ opacity: 0, x: 50 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.5 }}
                >
                    <h2>🌾 Recommended Crop:</h2>
                    <p className="crop-name">{recommendedCrop}</p>
                    {cropImage && (
                        <motion.img
                            src={cropImage}
                            alt={recommendedCrop}
                            className="crop-image"
                            initial={{ scale: 0.8, opacity: 0 }}
                            animate={{ scale: 1, opacity: 1 }}
                            transition={{ duration: 0.5 }}
                        />
                    )}
                    <ul className="bullet-list">
                        {Object.entries(cropDetails).map(([section, details]) => (
                            <li key={section}>
                                <strong>{section.replace(/_/g, " ")}:</strong>
                                <ul>
                                    {Object.entries(details).map(([key, value]) => (
                                        <li key={key}>
                                            {key.charAt(0).toUpperCase() + key.slice(1)}: {value}
                                        </li>
                                    ))}
                                </ul>
                            </li>
                        ))}
                    </ul>
                </motion.div>
            )}

            {error && (
                <motion.div
                    className="error-container"
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ duration: 0.3 }}
                >
                    <h2 className="error">⚠️ {error}</h2>
                </motion.div>
            )}
        </div>
    );
};

export default CropPrediction;
