import React, { useState } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

const CropPrediction = () => {
    const [formData, setFormData] = useState({
        N: "",
        P: "",
        K: "",
        temperature: "",
        humidity: "",
        ph: "",
        rainfall: "",
    });
    const navigate = useNavigate();

    const [cropDetails, setCropDetails] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [showResults, setShowResults] = useState(false);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setCropDetails(null);
        setError(null);
        setLoading(true);
        setShowResults(false);

        try {
            // First, get the prediction from the predict endpoint
            const predictResponse = await fetch("http://localhost:5000/api/auth/predict", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    N: parseFloat(formData.N),
                    P: parseFloat(formData.P),
                    K: parseFloat(formData.K),
                    temperature: parseFloat(formData.temperature),
                    humidity: parseFloat(formData.humidity),
                    ph: parseFloat(formData.ph),
                    rainfall: parseFloat(formData.rainfall),
                }),
            });

            const predictData = await predictResponse.json();
            
            if (!predictResponse.ok) {
                setLoading(false);
                setError(predictData.message || "Error predicting crop");
                return;
            }
            
            const cropName = predictData.recommended_crop;
            
            // Now fetch the crop details using the cropName
            const detailsResponse = await fetch(`http://localhost:5000/api/auth/get_crop/${cropName}`);
            const detailsData = await detailsResponse.json();
            
            setLoading(false);
            
            if (detailsResponse.ok) {
                setCropDetails({
                    name: cropName,
                    details: detailsData,
                    imagePath: `src/components/Assets/Images/rice.png`
                });
                // Trigger the animation after successful prediction
                setTimeout(() => setShowResults(true), 100);
            } else {
                setError("Error fetching crop details");
            }
        } catch (err) {
            setLoading(false);
            setError("Failed to connect to the backend");
        }
    };

    // Format the crop details as points
    const formatDetailsAsPoints = (details) => {
        if (!details || !details.description) return [];
        
        try {
            const parsedData = JSON.parse(details.description);
            let points = [];
            
            // Extract all points from each category
            Object.keys(parsedData).forEach(category => {
                const categoryData = parsedData[category];
                
                Object.values(categoryData).forEach(point => {
                    points.push(point);
                });
            });
            
            return points;
        } catch (err) {
            console.error("Error parsing crop details:", err);
            return ["Unable to parse crop details"];
        }
    };

    return (
        <div style={styles.pageContainer}>
            {/* Home Button */}
            <button onClick={() => navigate("/home")} style={styles.homeButton}>
                ‚¨Ö Home
            </button>

            {/* Form Component */}
            <motion.div
                style={styles.formContainer}
                initial={{ opacity: 0, y: 30 }}
                animate={{ 
                    opacity: 1, 
                    y: 0,
                    x: showResults ? "-25%" : "0%",
                    width: showResults ? "40%" : "600px" 
                }}
                transition={{ duration: 0.5, ease: "easeInOut" }}
            >
                <h1 style={styles.header}>üå± Crop Prediction</h1>
                
                <form style={styles.form} onSubmit={handleSubmit}>
                    <div style={styles.row}>
                        {["N", "P", "K"].map((key) => (
                            <div key={key} style={styles.inputGroup}>
                                <label style={styles.label} htmlFor={key}>
                                    {key}:
                                </label>
                                <input
                                    type="number"
                                    id={key}
                                    name={key}
                                    value={formData[key]}
                                    onChange={handleChange}
                                    style={styles.input}
                                    required
                                />
                            </div>
                        ))}
                    </div>

                    {["temperature", "humidity", "ph", "rainfall"].map((key) => (
                        <div key={key} style={styles.inputGroup}>
                            <label style={styles.label} htmlFor={key}>
                                {key.charAt(0).toUpperCase() + key.slice(1)}:
                            </label>
                            <input
                                type="number"
                                id={key}
                                name={key}
                                value={formData[key]}
                                onChange={handleChange}
                                style={styles.input}
                                required
                            />
                        </div>
                    ))}

                    <motion.button
                        type="submit"
                        style={styles.submitButton}
                        disabled={loading}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                    >
                        {loading ? "Processing..." : "Predict Crop"}
                    </motion.button>
                </form>

                {error && (
                    <motion.div
                        style={styles.errorContainer}
                        initial={{ scale: 0.8, opacity: 0 }}
                        animate={{ scale: 1, opacity: 1 }}
                        transition={{ duration: 0.3 }}
                    >
                        <h2 style={styles.error}>‚ö†Ô∏è {error}</h2>
                    </motion.div>
                )}
            </motion.div>

            {/* Results Component */}
            {cropDetails && (
                <motion.div
                    style={styles.resultContainer}
                    initial={{ opacity: 0, x: "100%" }}
                    animate={{ 
                        opacity: showResults ? 1 : 0, 
                        x: showResults ? "25%" : "100%",
                        width: "40%"
                    }}
                    transition={{ duration: 0.5, ease: "easeInOut" }}
                >
                    <h2 style={styles.result}>üåæ Recommended Crop</h2>
                    <p style={styles.cropName}>{cropDetails.name}</p>
                    
                    <div style={styles.detailsContainer}>
                        <div style={styles.imageContainer}>
                            <img 
                                src={cropDetails.imagePath} 
                                alt={cropDetails.name}
                                style={styles.cropImage}
                            />
                        </div>
                        
                        <div style={styles.cropInfo}>
                            {formatDetailsAsPoints(cropDetails.details).map((point, index) => (
                                <div key={index} style={styles.pointItem}>
                                    <span style={styles.bulletPoint}>‚Ä¢</span>
                                    <span style={styles.pointText}>{point}</span>
                                </div>
                            ))}
                        </div>
                    </div>
                </motion.div>
            )}
        </div>
    );
};

const styles = {
    pageContainer: {
        height: "100vh",
        width: "100vw",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: "#1a1a1a", // Dark grayish-black background
        color: "#ff7b42",
        overflow: "hidden", // Prevents scrolling during animation
    },
    formContainer: {
        width: "600px",
        padding: "30px",
        borderRadius: "12px",
        boxShadow: "0px 10px 20px #ff7b42",
        textAlign: "center",
        backgroundColor: "#222", // Slightly lighter grayish-black
        maxHeight: "90vh",
        overflowY: "auto",
        position: "relative", // For absolute positioning of child elements
    },
    header: {
        fontSize: "22px",
        fontWeight: "bold",
        color: "#ff7b42",
        marginBottom: "15px",
    },
    form: {
        display: "flex",
        flexDirection: "column",
        gap: "15px",
        width: "100%",
    },
    row: {
        display: "flex",
        justifyContent: "space-between",
        gap: "10px",
    },
    inputGroup: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        width: "100%",
    },
    label: {
        fontWeight: "600",
        color: "#ff7b42",
        marginBottom: "5px",
    },
    homeButton: {
        position: "absolute",
        top: "20px",
        left: "20px",
        padding: "10px 15px",
        fontSize: "14px",
        fontWeight: "bold",
        color: "#ff7b42",
        backgroundColor: "transparent",
        border: "2px solid #ff7b42",
        borderRadius: "5px",
        cursor: "pointer",
        transition: "background 0.3s, color 0.3s",
    },
    input: {
        width: "90%",
        padding: "8px",
        borderRadius: "6px",
        border: "1px solid #ff7b42",
        fontSize: "14px",
        backgroundColor: "rgba(0, 0, 0, 0.6)",
        color: "#ff7b42",
        transition: "border-color 0.3s, transform 0.3s",
        boxSizing: "border-box",
    },
    submitButton: {
        width: "100%",
        padding: "12px",
        fontSize: "16px",
        fontWeight: "bold",
        color: "#000",
        backgroundColor: "#ff7b42",
        border: "none",
        borderRadius: "6px",
        cursor: "pointer",
        transition: "background 0.3s, transform 0.3s",
    },
    resultContainer: {
        padding: "30px",
        backgroundColor: "#222",
        borderRadius: "12px",
        boxShadow: "0px 10px 20px #ff7b42",
        textAlign: "center",
        maxHeight: "90vh",
        overflowY: "auto",
    },
    result: {
        color: "#ff7b42",
        fontWeight: "bold",
        fontSize: "22px",
