import React, { useState } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

const CropPrediction = () => {
    const [formData, setFormData] = useState({
        N: "",
        P: "",
        K: "",
        temperature: "",
        humidity: "",
        ph: "",
        rainfall: "",
    });
    const navigate = useNavigate();

    const [cropDetails, setCropDetails] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [animateResults, setAnimateResults] = useState(false);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setCropDetails(null);
        setError(null);
        setLoading(true);
        setAnimateResults(false);

        try {
            // First, get the prediction from the predict endpoint
            const predictResponse = await fetch("http://localhost:5000/api/auth/predict", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    N: parseFloat(formData.N),
                    P: parseFloat(formData.P),
                    K: parseFloat(formData.K),
                    temperature: parseFloat(formData.temperature),
                    humidity: parseFloat(formData.humidity),
                    ph: parseFloat(formData.ph),
                    rainfall: parseFloat(formData.rainfall),
                }),
            });

            const predictData = await predictResponse.json();
            
            if (!predictResponse.ok) {
                setLoading(false);
                setError(predictData.message || "Error predicting crop");
                return;
            }
            
            const cropName = predictData.recommended_crop;
            
            // Now fetch the crop details using the cropName
            const detailsResponse = await fetch(`http://localhost:5000/api/auth/get_crop/${cropName}`);
            const detailsData = await detailsResponse.json();
            
            setLoading(false);
            
            if (detailsResponse.ok) {
                setCropDetails({
                    name: cropName,
                    details: detailsData,
                    imagePath: `src/components/Assets/Images/rice.png`
                });
                // Set animation flag to true after successful prediction
                setTimeout(() => setAnimateResults(true), 100);
            } else {
                setError("Error fetching crop details");
            }
        } catch (err) {
            setLoading(false);
            setError("Failed to connect to the backend");
        }
    };

    // Format the crop details as points
    const formatDetailsAsPoints = (details) => {
        if (!details || !details.description) return [];
        
        try {
            const parsedData = JSON.parse(details.description);
            let points = [];
            
            // Extract all points from each category
            Object.keys(parsedData).forEach(category => {
                const categoryData = parsedData[category];
                
                Object.values(categoryData).forEach(point => {
                    points.push(point);
                });
            });
            
            return points;
        } catch (err) {
            console.error("Error parsing crop details:", err);
            return ["Unable to parse crop details"];
        }
    };

    return (
        <div style={styles.pageContainer}>
            {/* Home Button */}
            <button onClick={() => navigate("/home")} style={styles.homeButton}>
                ⬅ Home
            </button>
            <motion.div
                style={styles.container}
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
                className={cropDetails && animateResults ? "results-active" : ""}
            >
                <h1 style={styles.header}>🌱 Crop Prediction</h1>
            
                <motion.div
                    style={{
                        ...styles.formContainer,
                        width: "100%"
                    }}
                    animate={cropDetails && animateResults ? { 
                        x: "-50%", 
                        width: "45%"
                    } : { 
                        x: "0%", 
                        width: "100%" 
                    }}
                    transition={{ duration: 0.5, ease: "easeInOut" }}
                >
                    <form style={styles.form} onSubmit={handleSubmit}>
                        <div style={styles.row}>
                            {["N", "P", "K"].map((key) => (
                                <div key={key} style={styles.inputGroup}>
                                    <label style={styles.label} htmlFor={key}>
                                        {key}:
                                    </label>
                                    <input
                                        type="number"
                                        id={key}
                                        name={key}
                                        value={formData[key]}
                                        onChange={handleChange}
                                        style={styles.input}
                                        required
                                    />
                                </div>
                            ))}
                        </div>

                        {["temperature", "humidity", "ph", "rainfall"].map((key) => (
                            <div key={key} style={styles.inputGroup}>
                                <label style={styles.label} htmlFor={key}>
                                    {key.charAt(0).toUpperCase() + key.slice(1)}:
                                </label>
                                <input
                                    type="number"
                                    id={key}
                                    name={key}
                                    value={formData[key]}
                                    onChange={handleChange}
                                    style={styles.input}
                                    required
                                />
                            </div>
                        ))}

                        <motion.button
                            type="submit"
                            style={styles.submitButton}
                            disabled={loading}
                            whileHover={{ scale: 1.05 }}
                            whileTap={{ scale: 0.95 }}
                        >
                            {loading ? "Processing..." : "Predict Crop"}
                        </motion.button>
                    </form>
                </motion.div>

                {cropDetails && (
                    <motion.div
                        style={styles.resultContainer}
                        initial={{ opacity: 0, x: "100%" }}
                        animate={animateResults ? { 
                            opacity: 1, 
                            x: "50%",
                            width: "45%" 
                        } : { 
                            opacity: 0, 
                            x: "100%",
                            width: "0%" 
                        }}
                        transition={{ duration: 0.5, ease: "easeInOut" }}
                    >
                        <h2 style={styles.result}>🌾 Recommended Crop</h2>
                        <p style={styles.cropName}>{cropDetails.name}</p>
                        
                        <div style={styles.detailsContainer}>
                            <div style={styles.imageContainer}>
                                <img 
                                    src={cropDetails.imagePath} 
                                    alt={cropDetails.name}
                                    style={styles.cropImage}
                                />
                            </div>
                            
                            <div style={styles.cropInfo}>
                                {formatDetailsAsPoints(cropDetails.details).map((point, index) => (
                                    <div key={index} style={styles.pointItem}>
                                        <span style={styles.bulletPoint}>•</span>
                                        <span style={styles.pointText}>{point}</span>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </motion.div>
                )}
                
                {error && (
                    <motion.div
                        style={styles.errorContainer}
                        initial={{ scale: 0.8, opacity: 0 }}
                        animate={{ scale: 1, opacity: 1 }}
                        transition={{ duration: 0.3 }}
                    >
                        <h2 style={styles.error}>⚠️ {error}</h2>
                    </motion.div>
                )}
            </motion.div>
        </div>
    );
};

const styles = {
    pageContainer: {
        height: "100vh",
        width: "100vw",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: "#1a1a1a", // Dark grayish-black background
        color: "#ff7b42",
        overflow: "hidden", // Prevents scrolling during animation
    },
    container: {
        width: "600px",
        padding: "30px",
        borderRadius: "12px",
        boxShadow: "0px 10px 20px #ff7b42",
        textAlign: "center",
        backgroundColor: "#222", // Slightly lighter grayish-black
        maxHeight: "90vh",
        overflowY: "auto",
        position: "relative", // For absolute positioning of child elements
        display: "flex",
        flexWrap: "wrap",
        justifyContent: "center",
    },
    formContainer: {
        transition: "all 0.5s ease-in-out",
    },
    header: {
        fontSize: "22px",
        fontWeight: "bold",
        color: "#ff7b42",
        marginBottom: "15px",
        width: "100%", // Ensure header takes full width
    },
    form: {
        display: "flex",
        flexDirection: "column",
        gap: "15px",
        width: "100%",
    },
    row: {
        display: "flex",
        justifyContent: "space-between",
        gap: "10px",
    },
    inputGroup: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        width: "100%",
    },
    label: {
        fontWeight: "600",
        color: "#ff7b42",
        marginBottom: "5px",
    },
    homeButton: {
        position: "absolute",
        top: "20px",
        left: "20px",
        padding: "10px 15px",
        fontSize: "14px",
        fontWeight: "bold",
        color: "#ff7b42",
        backgroundColor: "transparent",
