import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Signup.css';

const Signup = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [action, setAction] = useState('Sign Up');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = action === 'Sign Up' ? 'register' : 'login';
    try {
      await axios.post(`http://localhost:5001/api/auth/${endpoint}`, { username, email, password });
      alert(`${action} successful!`);
      navigate('/home');
    } catch (error) {
      alert(`${action} failed: ${error.response?.data?.message || error.message}`);
    }
  };

  return (
    <div className="page-container">
      {/* Background video */}
      <video autoPlay muted loop className="background-video">
        <source src="/homevideo.mp4" type="video/mp4" />
      </video>

      {/* Overlay for better contrast */}
      <div className="background-overlay"></div>

      <div className="content-container">
        <div className="branding-container">
          <div className="branding-title">
            {action === 'Sign Up' ? 'Welcome to' : 'Welcome Back'}
            <span className="brand-highlight"> EasyFarm</span>
          </div>
          <div className="branding-title-underline"></div>
          <p className="branding-description">
            {action === 'Sign Up'
              ? 'Join the EasyFarm community and start predicting your crops with ease.'
              : 'Log in to continue your farming journey.'}
          </p>
        </div>

        <div className="form-container">
          <div className="header">
            {action}
          </div>
          <div className="sub-header">
            {action === 'Sign Up'
              ? 'Create an account to get started!'
              : 'Welcome back! Please enter your credentials.'}
          </div>
          <form onSubmit={handleSubmit} className="form">
            {action === 'Sign Up' && (
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
                className="input-field"
              />
            )}
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className="input-field"
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="input-field"
            />
            <button type="submit" className="submit-button">
              {action}
            </button>
          </form>
          <button
            className="home-button"
            onClick={() => setAction(action === 'Sign Up' ? 'Login' : 'Sign Up')}
          >
            {action === 'Sign Up' ? 'Login' : 'Sign Up'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Signup;
