import React, { useState } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

const CropPrediction = () => {
    const [formData, setFormData] = useState({
        N: "",
        P: "",
        K: "",
        temperature: "",
        humidity: "",
        ph: "",
        rainfall: "",
    });

    const navigate = useNavigate();

    const [recommendedCrop, setRecommendedCrop] = useState(null);
    const [cropImage, setCropImage] = useState(null);
    const [cropDesc, setCropDescription] = useState({}); // Store as object
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [formShifted, setFormShifted] = useState(false);


    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setRecommendedCrop(null);
        setError(null);
        setLoading(true);
    
        try {
            // Step 1: Predict Crop
            const predictResponse = await fetch("http://localhost:5000/api/auth/predict", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    N: parseFloat(formData.N),
                    P: parseFloat(formData.P),
                    K: parseFloat(formData.K),
                    temperature: parseFloat(formData.temperature),
                    humidity: parseFloat(formData.humidity),
                    ph: parseFloat(formData.ph),
                    rainfall: parseFloat(formData.rainfall),
                }),
            });
    
            const predictData = await predictResponse.json();
    
            if (!predictResponse.ok) {
                setLoading(false);
                setError(predictData.message || "Error predicting crop");
                return;
            }
    
            const cropName = predictData.recommended_crop;
            setRecommendedCrop(cropName);
    
            // Step 2: Fetch Crop Details
            const detailsResponse = await fetch(`http://localhost:5001/api/auth/get_crop/${cropName}`);
            const detailsData = await detailsResponse.json();
    
            if (!detailsResponse.ok) {
                setLoading(false);
                setError(detailsData.message || "Error fetching crop details");
                return;
            }
    
            setCropDescription(JSON.parse(detailsData.description));
            setFormShifted(true);
            setLoading(false);
        } catch (err) {
            setLoading(false);
            setError("Failed to connect to the backend");
        }
    };
    

    return (
        <div style={styles.pageContainer}>
            {/* Home Button */}
            <button onClick={() => navigate("/home")} style={styles.homeButton}>
                ⬅ Home
            </button>

            {/* Form Animation */}
            <motion.div
                style={{
                    ...styles.container,
                    left: formShifted ? "-20%" : "0",
                    position: "relative",
                }}
                initial={{ x: 0, opacity: 1 }}
                animate={{
                    x: formShifted ? "-50%" : 0,
                    opacity: 1,
                }}
                transition={{ duration: 0.5 }}
            >
                <h1 style={styles.header}>🌱 Crop Prediction</h1>

                <form style={styles.form} onSubmit={handleSubmit}>
                    <div style={styles.row}>
                        {["N", "P", "K"].map((key) => (
                            <div key={key} style={styles.inputGroup}>
                                <label style={styles.label} htmlFor={key}>
                                    {key}:
                                </label>
                                <input
                                    type="number"
                                    id={key}
                                    name={key}
                                    value={formData[key]}
                                    onChange={handleChange}
                                    style={styles.input}
                                    required
                                />
                            </div>
                        ))}
                    </div>

                    {["temperature", "humidity", "ph", "rainfall"].map((key) => (
                        <div key={key} style={styles.inputGroup}>
                            <label style={styles.label} htmlFor={key}>
                                {key.charAt(0).toUpperCase() + key.slice(1)}:
                            </label>
                            <input
                                type="number"
                                id={key}
                                name={key}
                                value={formData[key]}
                                onChange={handleChange}
                                style={styles.input}
                                required
                            />
                        </div>
                    ))}

                    <motion.button
                        type="submit"
                        style={styles.submitButton}
                        disabled={loading}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                    >
                        {loading ? "Predicting..." : "Predict Crop"}
                    </motion.button>
                </form>
            </motion.div>

            {/* Prediction Result */}
            {recommendedCrop && (
                <motion.div
                    style={styles.resultContainer}
                    initial={{ opacity: 0, x: 50 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.5 }}
                >
                    <h2 style={styles.result}>🌾 Recommended Crop:</h2>
                    <p style={styles.cropName}>{recommendedCrop}</p>
                    {cropImage && (
                        <motion.img
                            src={cropImage}
                            alt={recommendedCrop}
                            style={styles.cropImage}
                            initial={{ scale: 0.8, opacity: 0 }}
                            animate={{ scale: 1, opacity: 1 }}
                            transition={{ duration: 0.5 }}
                        />
                    )}
                    {cropDescription && (
                        <p style={styles.cropDescription}>{cropDescription}</p>
                    )}
                </motion.div>
            )}
            {cropDetails && (
                <motion.div
                    style={styles.resultContainer}
                    initial={{ opacity: 0, x: 50 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.5 }}
                >
                    <h2 style={styles.result}>🌾 Recommended Crop:</h2>
                    <p style={styles.cropName}>{recommendedCrop}</p>
                    {cropImage && (
                        <motion.img
                            src={cropImage}
                            alt={recommendedCrop}
                            style={styles.cropImage}
                            initial={{ scale: 0.8, opacity: 0 }}
                            animate={{ scale: 1, opacity: 1 }}
                            transition={{ duration: 0.5 }}
                        />
                    )}
                    <ul style={styles.bulletList}>
                        <li>
                            <strong>🌍 Soil and Climate:</strong>
                            <ul>
                                <li>Soil Type: {cropDetails.soil_and_climate.soil_type}</li>
                                <li>Temperature: {cropDetails.soil_and_climate.temperature}</li>
                                <li>Rainfall: {cropDetails.soil_and_climate.rainfall}</li>
                            </ul>
                        </li>
                        <li>
                            <strong>🌱 Sowing and Cultivation:</strong>
                            <ul>
                                <li>Sowing Time: {cropDetails.sowing_and_cultivation.sowing_time}</li>
                                <li>Seed Rate: {cropDetails.sowing_and_cultivation.seed_rate}</li>
                                <li>Spacing: {cropDetails.sowing_and_cultivation.spacing}</li>
                            </ul>
                        </li>
                        <li>
                            <strong>💧 Irrigation and Fertilization:</strong>
                            <ul>
                                <li>Irrigation: {cropDetails.irrigation_and_fertilization.irrigation}</li>
                                <li>Fertilizers: {cropDetails.irrigation_and_fertilization.fertilizers}</li>
                            </ul>
                        </li>
                        <li>
                            <strong>🌾 Harvesting and Yield:</strong>
                            <ul>
                                <li>Harvest Time: {cropDetails.harvesting_and_yield.harvest_time}</li>
                                <li>Yield: {cropDetails.harvesting_and_yield.yield}</li>
                            </ul>
                        </li>
                        <li>
                            <strong>💲 Market Price:</strong>
                            <ul>
                                <li>Average Price per Quintal: {cropDetails.market_price.average_price_per_quintal}</li>
                            </ul>
                        </li>
                    </ul>
                </motion.div>
            )}


            {error && (
                <motion.div
                    style={styles.errorContainer}
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ duration: 0.3 }}
                >
                    <h2 style={styles.error}>⚠️ {error}</h2>
                </motion.div>
            )}
        </div>
    );
};

