import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import "./CropPrediction.css"; // Import the CSS

const CropPrediction = () => {
    const [formData, setFormData] = useState({
        N: "",
        P: "",
        K: "",
        temperature: "",
        humidity: "",
        ph: "",
        rainfall: "",
    });

    const navigate = useNavigate();

    const [recommendedCrop, setRecommendedCrop] = useState(null);
    const [cropImage, setCropImage] = useState(null);
    const [cropDetails, setCropDetails] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [formShifted, setFormShifted] = useState(false);

    // Handle input changes
    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        setRecommendedCrop(null);
        setError(null);
        setLoading(true);

        try {
            // Step 1: Predict Crop
            const predictResponse = await fetch("http://localhost:5000/api/auth/predict", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    N: parseFloat(formData.N),
                    P: parseFloat(formData.P),
                    K: parseFloat(formData.K),
                    temperature: parseFloat(formData.temperature),
                    humidity: parseFloat(formData.humidity),
                    ph: parseFloat(formData.ph),
                    rainfall: parseFloat(formData.rainfall),
                }),
            });

            const predictData = await predictResponse.json();

            if (!predictResponse.ok) {
                setLoading(false);
                setError(predictData.message || "Error predicting crop");
                return;
            }

            const cropName = predictData.recommended_crop;
            setRecommendedCrop(cropName);

            // Step 2: Fetch Crop Details
            const detailsResponse = await fetch(`http://localhost:5001/api/auth/get_crop/${cropName}`);
            const detailsData = await detailsResponse.json();

            if (!detailsResponse.ok) {
                setLoading(false);
                setError(detailsData.message || "Error fetching crop details");
                return;
            }

            setCropDetails(JSON.parse(detailsData.description));
            setFormShifted(true);
            setLoading(false);
        } catch (err) {
            setLoading(false);
            setError("Failed to connect to the backend");
        }
    };

    // Load crop image dynamically
    useEffect(() => {
        if (recommendedCrop) {
            try {
                const image = require(`../Asset/Images/${recommendedCrop.toLowerCase()}.png`);
                setCropImage(image);
            } catch (err) {
                console.error("Error loading crop image:", err);
                setCropImage(null);
            }
        }
    }, [recommendedCrop]);

    return (
        <div className="page-container">
            {/* Home Button */}
            <button onClick={() => navigate("/home")} className="home-button">
                ‚¨Ö Home
            </button>

            {/* Form Animation */}
            <motion.div
                className="form-container"
                initial={{ x: 0, opacity: 1 }}
                animate={{
                    x: formShifted ? "-50%" : 0,
                    opacity: 1,
                }}
                transition={{ duration: 0.5 }}
            >
                <h1 className="header">üå± Crop Prediction</h1>

                <form className="form" onSubmit={handleSubmit}>
                    <div className="row">
                        {["N", "P", "K"].map((key) => (
                            <div key={key} className="input-group">
                                <label htmlFor={key}>{key}:</label>
                                <input
                                    type="number"
                                    id={key}
                                    name={key}
                                    value={formData[key]}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                        ))}
                    </div>

                    {["temperature", "humidity", "ph", "rainfall"].map((key) => (
                        <div key={key} className="input-group">
                            <label htmlFor={key}>
                                {key.charAt(0).toUpperCase() + key.slice(1)}:
                            </label>
                            <input
                                type="number"
                                id={key}
                                name={key}
                                value={formData[key]}
                                onChange={handleChange}
                                required
                            />
                        </div>
                    ))}

                    <motion.button
                        type="submit"
                        className="submit-button"
                        disabled={loading}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                    >
                        {loading ? "Predicting..." : "Predict Crop"}
                    </motion.button>
                </form>
            </motion.div>

            {/* Prediction Result */}
            {recommendedCrop && (
                <motion.div
                    className="result-container"
                    initial={{ opacity: 0, x: 50 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.5 }}
                >
                    <h2>üåæ Recommended Crop:</h2>
                    <p className="crop-name">{recommendedCrop}</p>
                    {cropImage && (
                        <motion.img
                            src={cropImage}
                            alt={recommendedCrop}
                            className="crop-image"
                            initial={{ scale: 0.8, opacity: 0 }}
                            animate={{ scale: 1, opacity: 1 }}
                            transition={{ duration: 0.5 }}
                        />
                    )}
                    {cropDetails && (
                        <ul className="details-list">
                            <li>
                                <strong>üåç Soil and Climate:</strong>
                                <ul>
                                    <li>Soil Type: {cropDetails.soil_and_climate?.soil_type}</li>
                                    <li>Temperature: {cropDetails.soil_and_climate?.temperature}</li>
                                    <li>Rainfall: {cropDetails.soil_and_climate?.rainfall}</li>
                                </ul>
                            </li>
                            <li>
                                <strong>üå± Sowing and Cultivation:</strong>
                                <ul>
                                    <li>Sowing Time: {cropDetails.sowing_and_cultivation?.sowing_time}</li>
                                    <li>Seed Rate: {cropDetails.sowing_and_cultivation?.seed_rate}</li>
                                    <li>Spacing: {cropDetails.sowing_and_cultivation?.spacing}</li>
                                </ul>
                            </li>
                            <li>
                                <strong>üíß Irrigation and Fertilization:</strong>
                                <ul>
                                    <li>Irrigation: {cropDetails.irrigation_and_fertilization?.irrigation}</li>
                                    <li>Fertilizers: {cropDetails.irrigation_and_fertilization?.fertilizers}</li>
                                </ul>
                            </li>
                        </ul>
                    )}
                </motion.div>
            )}

            {/* Error Display */}
            {error && (
                <motion.div
                    className="error-container"
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ duration: 0.3 }}
                >
                    <h2 className="error">‚ö†Ô∏è {error}</h2>
                </motion.div>
            )}
        </div>
    );
};

export default CropPrediction;
