import React, { useState } from "react";
import { motion } from "framer-motion";

const CropPrediction = () => {
    const [formData, setFormData] = useState({
        N: "",
        P: "",
        K: "",
        temperature: "",
        humidity: "",
        ph: "",
        rainfall: "",
    });

    const [recommendedCrop, setRecommendedCrop] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setRecommendedCrop(null);
        setError(null);
        setLoading(true);

        try {
            const response = await fetch("http://localhost:5000/api/auth/predict", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    N: parseFloat(formData.N),
                    P: parseFloat(formData.P),
                    K: parseFloat(formData.K),
                    temperature: parseFloat(formData.temperature),
                    humidity: parseFloat(formData.humidity),
                    ph: parseFloat(formData.ph),
                    rainfall: parseFloat(formData.rainfall),
                }),
            });

            const data = await response.json();
            setLoading(false);

            if (response.ok) {
                setRecommendedCrop(data.recommended_crop);
            } else {
                setError(data.message || "Error predicting crop");
            }
        } catch (err) {
            setLoading(false);
            setError("Failed to connect to the backend");
        }
    };

    return (
        <div style={styles.pageContainer}>
            <motion.div
                style={styles.container}
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
            >
                <h1 style={styles.header}>🌱 Crop Prediction</h1>

                <form style={styles.form} onSubmit={handleSubmit}>
    {/* First row: N, P, K */}
    <div style={styles.inputRow}>
        {["N", "P", "K"].map((key) => (
            <div key={key} style={styles.inputGroup}>
                <label style={styles.label} htmlFor={key}>
                    {key}:
                </label>
                <input
                    type="number"
                    id={key}
                    name={key}
                    value={formData[key]}
                    onChange={handleChange}
                    style={styles.input}
                    required
                />
            </div>
        ))}
    </div>

    {/* Other inputs below */}
    {["temperature", "humidity", "ph", "rainfall"].map((key) => (
        <div key={key} style={styles.inputGroup}>
            <label style={styles.label} htmlFor={key}>
                {key.charAt(0).toUpperCase() + key.slice(1)}:
            </label>
            <input
                type="number"
                id={key}
                name={key}
                value={formData[key]}
                onChange={handleChange}
                style={styles.input}
                required
            />
        </div>
    ))}

    {/* Submit Button */}
    <button type="submit" style={styles.submitButton} disabled={loading}>
        {loading ? "Predicting..." : "Predict Crop"}
    </button>
</form>

                {recommendedCrop && (
                    <motion.div
                        style={styles.resultContainer}
                        initial={{ scale: 0.8, opacity: 0 }}
                        animate={{ scale: 1, opacity: 1 }}
                        transition={{ duration: 0.3 }}
                    >
                        <h2 style={styles.result}>🌾 Recommended Crop:</h2>
                        <p style={styles.cropName}>{recommendedCrop}</p>
                    </motion.div>
                )}
                {error && (
                    <motion.div
                        style={styles.errorContainer}
                        initial={{ scale: 0.8, opacity: 0 }}
                        animate={{ scale: 1, opacity: 1 }}
                        transition={{ duration: 0.3 }}
                    >
                        <h2 style={styles.error}>⚠️ {error}</h2>
                    </motion.div>
                )}
            </motion.div>
        </div>
    );
};

const styles = {
    pageContainer: {
        height: "100vh",
        width: "100vw",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: "#1a1a1a", // Dark grayish-black
        color: "#ff7b42", // Updated color
    },
    container: {
        width: "500px",
        padding: "30px",
        borderRadius: "12px",
        boxShadow: "0px 10px 20px rgba(255, 123, 66, 0.2)", // Adjusted shadow color
        textAlign: "center",
        backgroundColor: "#222",
    },
    header: {
        fontSize: "22px",
        fontWeight: "bold",
        color: "#ff7b42", // Updated color
        marginBottom: "15px",
    },
    label: {
        display: "block",
        fontWeight: "600",
        color: "#ff7b42", // Updated color
        marginBottom: "5px",
    },
    input: {
        width: "100%",
        padding: "10px",
        borderRadius: "6px",
        border: "1px solid #ff7b42", // Updated border color
        fontSize: "16px",
        backgroundColor: "rgba(0, 0, 0, 0.6)",
        color: "#ff7b42", // Updated text color
    },
    submitButton: {
        width: "100%",
        padding: "12px",
        fontSize: "16px",
        fontWeight: "bold",
        color: "#000",
        backgroundColor: "#ff7b42", // Updated button color
        border: "none",
        borderRadius: "6px",
        cursor: "pointer",
        transition: "background 0.3s, transform 0.3s",
    },
    result: {
        color: "#ff7b42", // Updated result color
        fontWeight: "bold",
    },
};


export default CropPrediction;
